use Illuminate\Contracts\Queue\ShouldBeUnique;
use Illuminate\Contracts\Queue\ShouldQueue;
use Illuminate\Foundation\Queue\Queueable;

class TeamSeasonStatisticJob extends AbstractSyncJob implements ShouldQueue, ShouldBeUnique
{
    use Queueable;

    const STAT_TYPE_OVERALL = 'overall';
    const STAT_TYPE_REGULAR = 'regularSeason';

    protected ?Team $team;

    protected Season $season;

    protected string $statType;

    protected bool $topTeams;

    /**
     * Create a new job instance.
     */
    public function __construct(Season $season, Team $team = null,string $statType = self::STAT_TYPE_OVERALL)
    {
        $this->team = $team;
        $this->season = $season;
        $this->statType = $statType;
    }

    public function uniqueId(): string
    {
        if (false === $this->team instanceof Team) {
            return get_class($this->season) . '-team-seasons-statistic-' .$this->season->id;
        }
        return get_class($this->team) . $this->team->id . get_class($this->season) . $this->season->id . '-team-seasons-statistic';
    }


    private function getUrl(?Team $team, Season $season, Tournament $tournament, string $courseEvents): string
    {
        //Top Season Teams
        if (false === $team instanceof Team) {
            return sprintf(
                'seasons/teams-statistics/result?unique_tournament_id=%s&seasons_id=%s&seasons_statistics_type=%s',
                $tournament->getSourceId(),
                $season->getSourceId(),
                $courseEvents
            );
        }

        //Single team statistics
        return sprintf(
            'teams/statistics/result?team_stat_type=%s&season_id=%s&team_id=%s&unique_tournament_id=%s',
            $courseEvents,
            $season->getSourceId(),
            $team->getSourceId(),
            $tournament->getSourceId()
        );

    }

    /**
     * Execute the job.
     */
    public function handle(): void
    {
        try {
            if (false === config('sync.syncInactive') && $this->season->getIsActive() === false) {
                info('Season is inactive', ['season' => $this->season->id]);
                return;
            }

            $lastSync = $this->season->getLastSync();

            if (!empty($lastSync) && $this->getLastSyncComparatorDate()->lessThan($lastSync)) {
                info('Season last sync is less than a day', ['season' => $this->season->id]);
                return;
            }

            $url = $this->getUrl($this->team, $this->season, $this->season->tournament, $this->statType);
            //$data = $this->getData($url);
            $data = $this->getExampleData();

            $data = $data['data'] ?? [];

            if (empty($data)) {
                info('Season top teams missing data', ['season' => $this->season->getId()]);
                return;
            }

            if (!$this->team instanceof Team) {
                $transformedData = $this->transformTeamSeasonStatistics($data);
                foreach ($transformedData as $teamData) {
                    $team = TeamBuilder::build($teamData['team'], $this->season->tournament->sport);
                    $team->save();

                    $teamSeasonStatistic = TeamSeasonStatisticBuilder::build($team, $this->season, $teamData['data']);
                    $teamSeasonStatistic->save();
                }
            } else {
                $data['type'] = $this->statType;
                $teamSeasonStatistic = TeamSeasonStatisticBuilder::build($this->team, $this->season, $data);
                $teamSeasonStatistic->save();
            }
        } catch (\Exception $e) {
            info('Error syncing team season statistics', [
                'season' => $this->season->getId(),
                'error' => $e->getMessage()
            ]);
            return;
        }
    }

    private function transformTeamSeasonStatistics(array $data): array
    {
        $teams = [];

        foreach ($data as $key => $statsArray) {
            foreach ($statsArray as $stat) {
                $teamId = $stat['team']['id'];

                if (!isset($teams[$teamId])) {
                    $teams[$teamId] = [
                        'team' => $stat['team'],
                        'data' => []
                    ];
                }

                if (!isset($teams[$teamId]['data']['type'])) {
                    $teams[$teamId]['data']['type'] = $this->statType;
                }

                $teams[$teamId]['data'][$key] = $stat['statistics'][$key] ?? null;


                if (isset($stat['statistics']['id'])) {
                    $teams[$teamId]['data']['id'] = $stat['statistics']['id'] ?? null;
                }

                if (isset($stat['statistics']['matches'])) {
                    $teams[$teamId]['data']['matches'] = $stat['statistics']['matches'] ?? null;
                }

                if (isset($stat['statistics']['awardedMatches'])) {
                    $teams[$teamId]['data']['awardedMatches'] = $stat['statistics']['awardedMatches'] ?? null;
                }

            }
        }

        return array_values($teams);
    }

